class Solution:
    def findInMountainArray(self, target: int, mountainArr: 'MountainArray') -&gt; int:
        # Step 1: Find the peak element index
        left, right = 0, mountainArr.length() - 1
        while left &lt; right:
            mid = (left + right) // 2
            if mountainArr.get(mid) &lt; mountainArr.get(mid + 1):
                left = mid + 1
            else:
                right = mid
        peak = left

        # Step 2: Binary search in the increasing part
        left, right = 0, peak
        while left &lt;= right:
            mid = (left + right) // 2
            mid_val = mountainArr.get(mid)
            if mid_val == target:
                return mid
            elif mid_val &lt; target:
                left = mid + 1
            else:
                right = mid - 1

        # Step 3: Binary search in the decreasing part
        left, right = peak + 1, mountainArr.length() - 1
        while left &lt;= right:
            mid = (left + right) // 2
            mid_val = mountainArr.get(mid)
            if mid_val == target:
                return mid
            elif mid_val &gt; target:
                left = mid + 1
            else:
                right = mid - 1
        return -1
