    def findWords(self, board: List[List[str]], words: List[str]) -&gt; List[str]:
        R , C = len(board) , len(board[0])
        visited = set()

        def dfs(i,j,idx):
            #base cases
            if idx == len(word):
                return True

            if i &lt; 0 or i &gt;= R or j &lt; 0 or j &gt;= C:
                return False

            if board[i][j]!=word[idx]:
                return False

            if (i,j) in visited:
                return False

            #add
            visited.add((i,j))

            #explore
            res = dfs(i+1,j,idx+1) or
                  dfs(i-1,j,idx+1) or
                  dfs(i,j+1,idx+1) or
                  dfs(i,j-1,idx+1)

            #remove
            visited.remove((i,j))

            return res

        ans = []
        for r in range(R):
            for c in range(C):
                for word in words:
                    if word not in ans and board[r][c] == word[0]:
                        if dfs(r, c, 0):
                            ans.append(word)
        return ans
