    def maximumCandies(self, candies: List[int], k: int) -&gt; int:
        def f(mid):                     #f - numChildren is a function of candies
            return sum((c//mid) for c in candies)

        l, r = 1, max(candies)
        ans = 0
        while l &lt;=r:
            mid = (l + r ) // 2
            if f(mid)&gt;=k:
                ans = mid
                l = mid +1
            else:
                r = mid -1
