    def longestCommonSubsequence(self, text1: str, text2: str) -&gt; int:
        m, n = len(text1), len(text2)

        # dp[i][j] := the length of LCS(text1[0..i), text2[0..j))
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        for i in range(m):
            for j in range(n):
                if text1[i] == text2[j]:
                    dp[i + 1][j + 1] = 1 + dp[i][j]
                else:
                    dp[i + 1][j + 1] = max(dp[i][j + 1], dp[i + 1][j])
        return dp[m][n]
