Python <pre><code>class Solution:
    def longestCommonSubsequence(self, text1: str, text2: str) -&gt; int:
        m, n = len(text1), len(text2)

        dp = [0] * (n + 1) # only allocate a row of n (text2 size)

        for i in range(m):
            prev = 0        # Reset prev for each row
            for j in range(n):
                cache = dp[j+1]  # Store current cell before overwriting
                if text1[i] == text2[j]:
                    dp[j + 1] = 1 + prev
                else:
                    dp[j + 1] = max(dp[j], cache)

                prev = cache # Update prev here, after using it

        return dp[n]
