Python <pre><code>from functools import cache

class Solution:
    def longestCommonSubsequence(self, text1: str, text2: str) -&gt; int:
        m , n = len(text1) , len(text2)

        @cache
        def f( i , j):
            if i &lt; 0 or j &lt; 0:
                return 0
            if text1[i] == text2[j]:
                return 1 + f(i-1,j-1)
            return max(f( i - 1,j), f( i, j - 1))
        return f(m - 1, n - 1)
